mapper.select:
SELECT key_value
FROM sys_data_item
WHERE status = 1 AND key_name = #{key} AND sys_data_group_id = #{groupId}

mapper.update:
UPDATE sys_data_item
SET status = 2
WHERE id = #{id}

mapper.select:
SELECT exists(
        SELECT *
        FROM sys_data_item
        WHERE status = 1 AND id != #{id} AND key_name = #{key} AND sys_data_group_id = #{groupId}
)
